class Enemies {
    constructor() {
        this.lesserEnemies;
    }

    moveEnemies(height, width) {
        for (let i = 0; i < this.lesserEnemies.length; i++) {
            this.lesserEnemies[i].y += 2.5;

            if (this.lesserEnemies[i].y > (height - 40)) {
                this.lesserEnemies[i].y = -20;
            }
            if (this.lesserEnemies[i].y == -20) {
                this.lesserEnemies[i].x = Math.random() * (width - 40);
            }
        }
    }

    enemyKilled(enemyIndex) {
        let temp = this.lesserEnemies[enemyIndex];
        this.lesserEnemies[enemyIndex] = this.lesserEnemies[this.lesserEnemies.length - 1];
        this.lesserEnemies[this.lesserEnemies.length - 1] = temp;
        this.lesserEnemies.pop();
    }

    spawnLesserEnemies(width) {
        let myInterval, countInterval = 0;
    
        myInterval = setInterval(() => {
            this.lesserEnemies.push(this.spawnEnemies(width));
            countInterval++;
    
            if (countInterval > 3) {
                clearInterval(myInterval);
            }
        }, 1000);
    }
    
    spawnEnemies(width) {
        return { x: Math.floor( Math.random() * (width - 40) ), y:  -20};
    }
}

module.exports = Enemies;