class Players {
    constructor(id, name, number) {
        this.player_id = id;
        this.player_name = name;
        this.player_number = number;
        this.player_life = 3;
        this.bullets = [];
        this.player_immune = false;
        this.player_score = 0;
        this.position = {x: 300 * this.player_number, y: 500};
    }

    playerReceivedDamage() {
        this.player_life--;
        this.player_immune = true;

        setTimeout(() => { 
            this.player_immune = false;
        }, 3000); // 3 seconds immunity after collision
    }

    moveBullets() {
        for (let i = 0; i < this.bullets.length; i++) {
            if (this.bullets[i]) {
                this.bullets[i].y -= 5;
                
                if (this.bullets[i].y < 0) {
                    let temp = this.bullets[i];
                    this.bullets[i] = this.bullets[this.bullets.length - 1];
                    this.bullets[this.bullets.length - 1] = temp;
                    this.bullets.pop();
                }
            }
        }
    }

    enemyKilled(bulletIndex) {
        this.player_score += 10;

        let temp = this.bullets[bulletIndex];
        this.bullets[bulletIndex] = this.bullets[this.bullets.length - 1];
        this.bullets[this.bullets.length - 1] = temp;
        this.bullets.pop();
    }
}

module.exports = Players;